sys_ui_action.script:
(function() {

	try {
		var inputs = {};
		inputs['application'] = current.file_application.sys_id + ''; // String 
		deleteGraph();
		// Start Asynchronously: Uncomment to run in background. Code snippet will not have access to outputs.
		// sn_fd.FlowAPI.getRunner().subflow('x_146833_creativ_0.generate_digital_twin').inBackground().withInputs(inputs).run();

		// Execute Synchronously: Run in foreground. Code snippet has access to outputs.
		var result = sn_fd.FlowAPI.getRunner().subflow('x_146833_creativ_0.generate_app_digital_twin').inBackground().withInputs(inputs).run();
		current.state = 'processing';
		current.update();
		action.setRedirectURL(current);
gs.addInfoMessage("Refresh the record in couple of minutes. Once flow execution is complete .gml file will get attached to this record.");
		// Current subflow has no outputs defined.		
	} catch (ex) {
		var message = ex.getMessage();
		gs.error(message);
	}

})();

function deleteGraph(){
	var tableName='x_146833_creativ_0_graph';
var grGraph = new GlideRecord(tableName);
var nQuery = 'file.sys_id' + current.sys_id;
grGraph.addEncodedQuery(nQuery);
grGraph.query();
while(grGraph.next()) {
	grGraph.deleteRecord();
}
}
